import React, { useState } from "react";
import NumberPicker from 'react-widgets/NumberPicker';
import { dynamoCleatService } from '../../services/serverless/DynamoCleatService';
import ClipLoader from 'react-spinners/ClipLoader';
import { Link } from "react-router-dom";
import './Chooser.css';

const Chooser = () => {

    const [width, setWidth] = useState(5);
    const [upper, setUpper] = useState('any');
    const [comfort, setComfort] = useState(5);
    const [lockdown, setLockdown] = useState(5);
    const [result, setResult] = useState([]);
    const [loading, setLoading] = useState(false);

    const uppers = [
        {value: 'knitted', text: "Knitted"},
        {value: 'leather', text: "Leather"},
        {value: 'synthetic', text: 'Synthetic'},
        {value: 'any', text: 'Any'}
    ];

    const options = uppers.map((option) => {
        return <option value={option.value}>{option.text}</option>
    });

    const getCleatsHandler = () => {
        setLoading(true);
        dynamoCleatService.getCleatsByValue(width, comfort, lockdown, upper)
            .then((response) => {
                setResult(response);
                setLoading(false);
            });
    }

    const resetValuesHandler = () => {
        setComfort(5);
        setWidth(5);
        setLockdown(5);
        setUpper('any');
    }

    return (
        <div className='chooser-container'>
            <div className='aspects-section'>
                <h2>Let's find the right cleats for you!</h2>

                <div className="width-section">
                    <h3>Width</h3>
                    <NumberPicker
                        value={width}
                        max={10}
                        min={0}
                        onChange={width => setWidth(width)}
                    />
                </div>
                <div className="comfort-section">
                    <h3>Comfort</h3>
                    <NumberPicker
                        value={comfort}
                        max={10}
                        min={0}
                        onChange={comfort => setComfort(comfort)}
                    />
                </div>
                <div className="lockdown-section">
                    <h3>Lockdown</h3>
                    <NumberPicker
                        value={lockdown}
                        max={10}
                        min={0}
                        onChange={lockdown => setLockdown(lockdown)}
                    />
                </div>

                <div className="upper-section">
                    <h3>Upper</h3>
                    <select
                        value={upper}
                        onChange={(e) => setUpper(e.target.value)}
                    >{options}</select>    
                </div>
                <br />

                <button className="get-cleats-button" onClick={getCleatsHandler}>Get Cleats</button>
                <br />
                <button className="reset-values-button" onClick={resetValuesHandler}>Reset Values</button>
                <br />
                <button className="reset-results-button" onClick={() => setResult([])}>Reset Results</button>
            </div>

            <div className='results-section'>
                {loading && 
                    <ClipLoader
                       color={'red'}
                       loading={true}
                       size={150}
                       aria-label='Loading Spinner'
                    />
                }
                {!loading &&    
                    <div className='result-cleats'>
                        <h1>Results:</h1>
                        <br />
                        {result.map((boot) => {
                            return (
                                <Link to={`/cleat/${boot.cleatName}`}>
                                    <div className="cleat-item">
                                        {boot.imageUrl && 
                                            <img className='cleat-image' src={boot.imageUrl} width={100} alt="idk" />
                                        }
                                        <h3 className="cleat-label">{boot.brand} {boot.cleatName}</h3>
                                        {/* <div className="val-container">
                                            <p className="cleat-item-vals">Width: {boot.width}</p>
                                            <p className="cleat-item-vals">Comfort: {boot.comfort}</p>
                                            <p className="cleat-item-vals">Lockdown: {boot.lockdown}</p>
                                            <p className="cleat-item-vals">Upper: {boot.upper}</p>
                                        </div> */}
                                    </div>
                                </Link>
                            )
                        })}
                    </div>
                }
            </div>
        </div>
    )
}

export default Chooser;
